import { all, call, fork, put, take } from 'redux-saga/effects'; // saga 의 이펙트들
import axios from 'axios';

//-------------------------------------------------- LOG_IN
function logInAPI() {
    return axios.post('/api/login');
}

function* logIn() {
    try {
        const result = yield call(logInAPI)
        /* call을 사용한 이유는?
        fork는 비동기 함수 호출이여서 
        여기서 쓰이면 result가 나오기 전에 다음 코드가 실행되기 때문에
        call(동기 함수 호출)을 사용 한 것입니다.
        */

        yield put({
            type: 'LOG_IN_SUCCESS',
            data: result.data
        });

    } catch (err) {
        yield put({
            type: 'LOG_IN_FAILURE',
            data: err.response.data
        });
    }

}

function* watchLogin() {
    yield take('LOG_IN_REQUEST', logIn); // take: LOG_IN 이라는 액션이 실행될 때까지 기다리겠다는 의미
}

//-------------------------------------------------- LOG_OUT
function logOutAPI() {
    return axios.post('/api/login');
}

function* logOut() {
    try {
        const result = yield call(logOutAPI)

        yield put({
            type: 'LOG_OUT_SUCCESS',
            data: result.data
        });

    } catch (err) {
        yield put({
            type: 'LOG_OUT_FAILURE',
            data: err.response.data
        });
    }

}

function* watchLogout() {
    yield take('LOG_OUT_REQUEST', logOut);
}

//-------------------------------------------------- ADD_POST
function addPostAPI() {
    return axios.post('/api/login');
}

function* addPost() {
    try {
        const result = yield call(addPostAPI)

        yield put({
            type: 'ADD_POST_SUCCESS',
            data: result.data
        });

    } catch (err) {
        yield put({
            type: 'ADD_POST_FAILURE',
            data: err.response.data
        });
    }

}

function* watchAddPost() {
    yield take('ADD_POST_REQUEST', addPost);
}


export default function* rootSaga() {
    yield all([
        fork(watchLogin),
        fork(watchLogout),
        fork(watchAddPost),
    ])
}
