import { all, takeLatest, call, fork, put } from "redux-saga/effects";
import axios from "axios";

import {
    ADD_POST_REQUEST, 
    ADD_POST_SUCCESS, 
    ADD_POST_FAILURE,

    ADD_COMMENT_REQUEST, 
    ADD_COMMENT_SUCCESS, 
    ADD_COMMENT_FAILURE,

} from '../reducers/post'

//-------------------------------------------------- ADD_POST
function addPostAPI(data) {
    return axios.post('/api/post', data);
}

function* addPost(action) {
    try {
        const result = yield call(addPostAPI, action.data)

        yield put({
            type: ADD_POST_SUCCESS,
            data: result.data
        });

    } catch (err) {
        yield put({
            type: ADD_POST_FAILURE,
            data: err.response.data
        });
    }

}

function* watchAddPost() {
    yield takeLatest(ADD_POST_REQUEST, addPost);
}
//-------------------------------------------------- ADD_COMMENT
function addCommentAPI(data) {
    return axios.post('/api/post', data);
}

function* addComment(action) {
    try {
        const result = yield call(addCommentAPI, action.data)

        yield put({
            type: ADD_COMMENT_SUCCESS,
            data: result.data
        });

    } catch (err) {
        yield put({
            type: ADD_COMMENT_FAILURE,
            data: err.response.data
        });
    }

}

function* watchAddComment() {
    yield takeLatest(ADD_COMMENT_REQUEST, addComment, 2000);
}

export default function* postSaga() {
    yield all([
        fork(watchAddPost),
        fork(watchAddComment),
    ])
}

/*
------------------------ takeLatest의 문제점

takeLatest 이펙트의 문제점은 여러번의 요청이
서버로 가기 때문에 응답은 한번만 오더라도
서버에 데이터가 중복되서 저장될 수 있습니다.
그런경우 새로고침시 화면에 데이터가 중복되서 나타날수 있습니다.

------------------------ throttle로 문제해결

이 문제를 해결할 수 있는 effects가 throttle 입니다.
아래는 2초동안 한번의 요청을 받겠다는 설정 입니다.

function* watchAddPost() {
    yield throttle('ADD_POST_REQUEST', addPost, 2000);
}
------------------------ 서버검증으로 문제해결

takeLatest를 사용하면서
서버에서의 검증을 통해서 연속된 요청의 문제를 해결할 수도 있습니다.
*/
