import React,{ useState, useCallback } from 'react';
import PropTypes from 'prop-types';
import { Button, Card, Popover, Avatar, List } from 'antd'
import { HeartTwoTone, EllipsisOutlined, HeartOutlined, MessageOutlined, RetweetOutlined } from '@ant-design/icons';
import { useSelector } from 'react-redux';

import PostImages from '../components/PostImages';

const PostCard = ({ post }) => {

    const [liked, setLiked] = useState(false);// 좋아요 버튼의 상태
    const [commentFormOpened, setCommentFormOpened] = useState(false); // 댓글창 열기 버튼의 상태

    const onToggleLike = useCallback(() => {
        setLiked((prev) => !prev) // true -> false, false -> true
    },[]);

    const onToggleComment = useCallback(() => {
        setCommentFormOpened((prev) => !prev)
    },[])

    const id = useSelector((state) => state.user.me?.id);

    return (
        <div style={{ marginBottom: 20 }}>
            <Card
                cover={post.Images[0] && <PostImages images={post.Images} />}
                actions={[ // 배열에 jsx 를 넣을 때는 항상 키가 필요합니다.
                    <RetweetOutlined key="retweet" />,

                    liked 
                        ? <HeartTwoTone twoToneColor="#eb2f96" key="heart" onClick={onToggleLike} /> 
                        : <HeartOutlined key="heart" onClick={onToggleLike} />,

                    <MessageOutlined key="comment" onClick={onToggleComment}/>,

                    <Popover key="more" content={(
                        <Button.Group>
                            { id && post.User.id === id ? (
                            <>
                            <Button>수정</Button>
                            <Button type="danger">삭제</Button>
                            </>
                            ):<Button>신고</Button> }
                        </Button.Group>
                    )}>
                        <EllipsisOutlined />
                    </Popover>,
                ]}
            >
                <Card.Meta
                avatar={ <Avatar>{ post.User.nickname[0] }</Avatar> }
                title={ post.User.nickname }
                description={ post.content }
                />
            </Card>
            {commentFormOpened && (
                <div>
                    <CommentForm />
                    <List />
                </div>
            )}
            {/* <CommentForm /> */}
            {/* <Comments /> */}
        </div>
    )
};

PostCard.propTypes = {
    post: PropTypes.shape({
        id: PropTypes.number,
        User: PropTypes.object,
        content: PropTypes.string,
        createdAt: PropTypes.object,
        Comments: PropTypes.arrayOf(PropTypes.object),
        Images: PropTypes.arrayOf(PropTypes.object),
    }).isRequired
}

export default PostCard;
