import shortId from 'shortid';

export const initialState = {
    mainPosts: [
        {
            id: 1,

            User: { id: 1, nickname: '해돋이' },

            content: '안녕하세요 트위터 짝퉁 궁금해서 가입해봤습니다~  #해시태그 #익스프레스',

            Images: [
                { src: 'https://bit.ly/34S7zRp' },
                { src: 'https://bit.ly/3uX6IJH' },
                { src: 'https://bit.ly/2TDFv1w' },
            ],

            Comments: [
                { User: { nickname: '재원' }, content: '키보드 윤활해야지~' },
                { User: { nickname: '선범' }, content: '여친이랑 산에 가야지~' },
            ],
        },
    ],
    imagePaths: [], // 이미지 업로드시 경로 저장

    addPostLoading: false,
    addPostDone: false,
    addPostError: null,

    addCommentLoading: false,
    addCommentDone: false,
    addCommentError: null, 
};
//-------------------------------------------- ADD_POST에 사용될 더미데이터
const dummyPost = (data) => ({
    id: shortId.generate(),
    content: data,
    User: {
        id: 1,
        nickname: '해돋이',
    },
    Images: [],
    Comments: [],
});

//-------------------------------------------- 액션
export const ADD_POST_REQUEST = 'ADD_POST_REQUEST';
export const ADD_POST_SUCCESS = 'ADD_POST_SUCCESS';
export const ADD_POST_FAILURE = 'ADD_POST_FAILURE';

export const ADD_COMMENT_REQUEST = 'ADD_COMMENT_REQUEST';
export const ADD_COMMENT_SUCCESS = 'ADD_COMMENT_SUCCESS';
export const ADD_COMMENT_FAILURE = 'ADD_COMMENT_FAILURE';

//--------------------------------------------
export const addPost = (data) => ({
    type: ADD_POST_REQUEST,
    data,
});

export const addComment = (data) => ({
    type: ADD_COMMENT_REQUEST,
    data,
});

const reducer = (state = initialState, action) => {
    switch (action.type) {
        //-------------------------------------- ADD_POST
        case ADD_POST_REQUEST:
            return {
                ...state,
                addPostLoading: true,
                addPostDone: false,
                addPostError: null,
            };
        case ADD_POST_SUCCESS:
            return {
                ...state,
                mainPosts: [dummyPost(action.data), ...state.mainPosts],
                addPostLoading: false,
                addPostDone: true,
            };
        case ADD_POST_FAILURE:
            return {
                ...state,
                addPostLoading: false,
                addPostError: action.error,
            };
        //-------------------------------------- ADD_COMMENT
        case ADD_COMMENT_REQUEST:
            return {
                ...state,
                addCommentLoading: true,
                addCommentDone: false,
                addCommentError: null,
            };
        case ADD_COMMENT_SUCCESS: {
            const postIndex = state.mainPosts.findIndex((v) => v.id === action.data.postId); // 댓글을 작성한 게시글의 인덱스 찾기
            const post = { ...state.mainPosts[postIndex] }; // 댓글을 작성한 게시글 얕은 복사
            post.Comments = [dummyComment(action.data.content),...post.Comments]; // 게시글의 댓글 얕은 복사하고 새 댓글 추가하기 
            const mainPosts = [...state.mainPosts]; // 게시글 얕은복사
            mainPosts[postIndex] = post; // 얕은 복사된 댓글 쓴 게시글에 댓글 추가한 게시글 할당
            return {
                ...state,
                addCommentLoading: false,
                addCommentDone: true,
            };
        }
        case ADD_COMMENT_FAILURE:
            return {
                ...state,
                addCommentLoading: false,
                addCommentError: action.error,
            };
        default:
            return state;
    }
};

export default reducer;
