import { all, call, fork, put, take  } from 'redux-saga/effects'; // saga 의 이펙트들
import axios from 'axios';

function loginAPI(){
    return axios.post('/api/login'); 
}

function* login() {
    const result = yield call(loginAPI)
    yield put({
        type: 'LOG_IN_SUCCESS',
        data: result.data
    })
}

function* watchLogin() {
    yield take('LOG_IN', login); // take: LOG_IN 이라는 액션이 실행될 때까지 기다리겠다는 의미
}

function* watchLogout() {
    yield take('LOG_OUT');
}

function* watchAddPost() {
    yield take('ADD_POST');
}

export default function* rootSaga() {
    yield all([
        fork(watchLogin),
        fork(watchLogout),
        fork(watchAddPost),
    ])
}
