export const initialState = {
    mainPosts: [{
        id: 1,
        User: {
            id: 1,
            nickname: '해돋이',
        },
        content: '안녕하세요 트위터 짝퉁 궁금해서 가입해봤습니다~  #해시태그 #익스프레스',
        Images: [
            { src: 'https://bit.ly/34S7zRp' },
            { src: 'https://bit.ly/3uX6IJH' },
            { src: 'https://bit.ly/2TDFv1w' },
        ],
        Comments: [{
            User: {
                nickname: '재원'
            },
            content: '키보드 윤활해야지~'
        }, {
            User: {
                nickname: '선범'
            },
            content: '여친이랑 산에 가야지~'
        }],
    }],
    imagePaths: [], // 이미지 업로드시 경로 저장
    postAdded: false, // 게시글 추가가 완료되었을 때 true 로 변환

}

const dummyPost = { // ADD_POST에 사용될 더미데이터
    id: 2,
    content: '더미데이터입니다.',
    User: {
        id: 1,
        nickname: '해돋이'
    },
    Images: [],
    Comments: [],
}

const ADD_POST = 'ADD_POST';
export const addPost = {
    type: ADD_POST,
};


const reducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_POST:
            return { 
                ...state,
                mainPosts: [dummyPost, ...state.mainPosts],// 가장 위로 올라오도록
            }
        default:
            return state;
    }
};

export default reducer;
